int parse_options(struct futhark_context_config *cfg, int argc,
                  char *const argv[])
{
    int ch;
    static struct option long_options[] = {{"debugging", no_argument, NULL, 1},
                                           {"log", no_argument, NULL, 2},
                                           {"help", no_argument, NULL, 3},
                                           {"print-params", no_argument, NULL,
                                            4}, {"param", required_argument,
                                                 NULL, 5}, {"tuning",
                                                            required_argument,
                                                            NULL, 6}, {"device",
                                                                       required_argument,
                                                                       NULL, 7},
                                           {"default-group-size",
                                            required_argument, NULL, 8},
                                           {"default-num-groups",
                                            required_argument, NULL, 9},
                                           {"default-tile-size",
                                            required_argument, NULL, 10},
                                           {"default-reg-tile-size",
                                            required_argument, NULL, 11},
                                           {"default-threshold",
                                            required_argument, NULL, 12},
                                           {"platform", required_argument, NULL,
                                            13}, {"dump-opencl",
                                                  required_argument, NULL, 14},
                                           {"load-opencl", required_argument,
                                            NULL, 15}, {"dump-opencl-binary",
                                                        required_argument, NULL,
                                                        16},
                                           {"load-opencl-binary",
                                            required_argument, NULL, 17},
                                           {"build-option", required_argument,
                                            NULL, 18}, {"profile", no_argument,
                                                        NULL, 19},
                                           {"list-devices", no_argument, NULL,
                                            20}, {0, 0, 0, 0}};
    static char *option_descriptions =
                "  -D/--debugging              Perform possibly expensive internal correctness checks and verbose logging.\n  -L/--log                    Print various low-overhead logging information while running.\n  -h/--help                   Print help information and exit.\n  --print-params              Print all tuning parameters that can be set with --param or --tuning.\n  --param ASSIGNMENT          Set a tuning parameter to the given value.\n  --tuning FILE               Read size=value assignments from the given file.\n  -d/--device NAME            Use the first OpenCL device whose name contains the given string.\n  --default-group-size INT    The default size of OpenCL workgroups that are launched.\n  --default-num-groups INT    The default number of OpenCL workgroups that are launched.\n  --default-tile-size INT     The default tile size used when performing two-dimensional tiling.\n  --default-reg-tile-size INT The default register tile size used when performing two-dimensional tiling.\n  --default-threshold INT     The default parallelism threshold.\n  -p/--platform NAME          Use the first OpenCL platform whose name contains the given string.\n  --dump-opencl FILE          Dump the embedded OpenCL program to the indicated file.\n  --load-opencl FILE          Instead of using the embedded OpenCL program, load it from the indicated file.\n  --dump-opencl-binary FILE   Dump the compiled version of the embedded OpenCL program to the indicated file.\n  --load-opencl-binary FILE   Load an OpenCL binary from the indicated file.\n  --build-option OPT          Add an additional build option to the string passed to clBuildProgram().\n  -P/--profile                Gather profiling data while executing and print out a summary at the end.\n  --list-devices              List all OpenCL devices and platforms available on the system.\n";
    
    while ((ch = getopt_long(argc, argv, ":DLhd:p:P", long_options, NULL)) !=
           -1) {
        if (ch == 1 || ch == 'D')
            futhark_context_config_set_debugging(cfg, 1);
        if (ch == 2 || ch == 'L')
            futhark_context_config_set_logging(cfg, 1);
        if (ch == 3 || ch == 'h') {
            printf("Usage: %s [OPTIONS]...\nOptions:\n\n%s\nFor more information, consult the Futhark User's Guide or the man pages.\n",
                   fut_progname, option_descriptions);
            exit(0);
        }
        if (ch == 4) {
            int n = futhark_get_tuning_param_count();
            
            for (int i = 0; i < n; i++)
                printf("%s (%s)\n", futhark_get_tuning_param_name(i),
                       futhark_get_tuning_param_class(i));
            exit(0);
        }
        if (ch == 5) {
            char *name = optarg;
            char *equals = strstr(optarg, "=");
            char *value_str = equals != NULL ? equals + 1 : optarg;
            int value = atoi(value_str);
            
            if (equals != NULL) {
                *equals = 0;
                if (futhark_context_config_set_tuning_param(cfg, name, value) !=
                    0)
                    futhark_panic(1, "Unknown size: %s\n", name);
            } else
                futhark_panic(1, "Invalid argument for size option: %s\n",
                              optarg);
        }
        if (ch == 6) {
            char *ret = load_tuning_file(optarg, cfg, (int (*)(void *, const
                                                               char *,
                                                               size_t)) futhark_context_config_set_tuning_param);
            
            if (ret != NULL)
                futhark_panic(1, "When loading tuning from '%s': %s\n", optarg,
                              ret);
        }
        if (ch == 7 || ch == 'd')
            futhark_context_config_set_device(cfg, optarg);
        if (ch == 8)
            futhark_context_config_set_default_group_size(cfg, atoi(optarg));
        if (ch == 9)
            futhark_context_config_set_default_num_groups(cfg, atoi(optarg));
        if (ch == 10)
            futhark_context_config_set_default_tile_size(cfg, atoi(optarg));
        if (ch == 11)
            futhark_context_config_set_default_reg_tile_size(cfg, atoi(optarg));
        if (ch == 12)
            futhark_context_config_set_default_threshold(cfg, atoi(optarg));
        if (ch == 13 || ch == 'p')
            futhark_context_config_set_platform(cfg, optarg);
        if (ch == 14) {
            futhark_context_config_dump_program_to(cfg, optarg);
            entry_point = NULL;
        }
        if (ch == 15)
            futhark_context_config_load_program_from(cfg, optarg);
        if (ch == 16) {
            futhark_context_config_dump_binary_to(cfg, optarg);
            entry_point = NULL;
        }
        if (ch == 17)
            futhark_context_config_load_binary_from(cfg, optarg);
        if (ch == 18)
            futhark_context_config_add_build_option(cfg, optarg);
        if (ch == 19 || ch == 'P')
            futhark_context_config_set_profiling(cfg, 1);
        if (ch == 20) {
            futhark_context_config_list_devices(cfg);
            entry_point = NULL;
        }
        if (ch == ':')
            futhark_panic(-1, "Missing argument for option %s\n", argv[optind -
                                                                       1]);
        if (ch == '?') {
            fprintf(stderr, "Usage: %s [OPTIONS]...\nOptions:\n\n%s\n",
                    fut_progname,
                    "  -D/--debugging              Perform possibly expensive internal correctness checks and verbose logging.\n  -L/--log                    Print various low-overhead logging information while running.\n  -h/--help                   Print help information and exit.\n  --print-params              Print all tuning parameters that can be set with --param or --tuning.\n  --param ASSIGNMENT          Set a tuning parameter to the given value.\n  --tuning FILE               Read size=value assignments from the given file.\n  -d/--device NAME            Use the first OpenCL device whose name contains the given string.\n  --default-group-size INT    The default size of OpenCL workgroups that are launched.\n  --default-num-groups INT    The default number of OpenCL workgroups that are launched.\n  --default-tile-size INT     The default tile size used when performing two-dimensional tiling.\n  --default-reg-tile-size INT The default register tile size used when performing two-dimensional tiling.\n  --default-threshold INT     The default parallelism threshold.\n  -p/--platform NAME          Use the first OpenCL platform whose name contains the given string.\n  --dump-opencl FILE          Dump the embedded OpenCL program to the indicated file.\n  --load-opencl FILE          Instead of using the embedded OpenCL program, load it from the indicated file.\n  --dump-opencl-binary FILE   Dump the compiled version of the embedded OpenCL program to the indicated file.\n  --load-opencl-binary FILE   Load an OpenCL binary from the indicated file.\n  --build-option OPT          Add an additional build option to the string passed to clBuildProgram().\n  -P/--profile                Gather profiling data while executing and print out a summary at the end.\n  --list-devices              List all OpenCL devices and platforms available on the system.\n");
            futhark_panic(1, "Unknown option: %s\n", argv[optind - 1]);
        }
    }
    return optind;
}
int main(int argc, char **argv)
{
    fut_progname = argv[0];
    
    struct futhark_context_config *cfg = futhark_context_config_new();
    
    assert(cfg != NULL);
    
    int parsed_options = parse_options(cfg, argc, argv);
    
    argc -= parsed_options;
    argv += parsed_options;
    if (argc != 0)
        futhark_panic(1, "Excess non-option: %s\n", argv[0]);
    
    struct futhark_context *ctx = futhark_context_new(cfg);
    
    assert(ctx != NULL);
    futhark_context_set_logging_file(ctx, stdout);
    
    char *error = futhark_context_get_error(ctx);
    
    if (error != NULL)
        futhark_panic(1, "Error during context initialisation:\n%s", error);
    if (entry_point != NULL)
        run_server(&prog, cfg, ctx);
    futhark_context_free(ctx);
    futhark_context_config_free(cfg);
}

